changeSet.sql:
IF EXISTS (SELECT * FROM dbo.sysobjects WHERE id = object_id(N'dbo.add_mail_equip_item') AND OBJECTPROPERTY(id, N'IsProcedure') = 1)
    DROP PROCEDURE dbo.add_mail_equip_item
GO


CREATE PROCEDURE dbo.add_mail_equip_item
    @mail_id dbo.id_t,
    @character_id dbo.id_t,
    @item_id dbo.id_t,
    @item_code dbo.code_t,
	@item_count TINYINT,
	@fragment_count TINYINT,
	@open_socket_count TINYINT,
	@skill_code_1 dbo.code_t,
	@skill_code_2 dbo.code_t,
	@skill_code_3 dbo.code_t,
	@skill_code_4 dbo.code_t,
	@skill_code_5 dbo.code_t
AS
    SET NOCOUNT ON

    BEGIN TRY
        BEGIN TRANSACTION

        INSERT INTO dbo.Mail_Inventories(mail_id, character_id, id, item_code, item_count)
	        VALUES (@mail_id, @character_id, @item_id, @item_code, @item_count)

		INSERT INTO dbo.Mail_Equip_Item_Infos(mail_id, character_id, item_id, fragment_count, open_socket_count, socket_skill_code_1,
			socket_skill_code_2,socket_skill_code_3,socket_skill_code_4, socket_skill_code_5)
			VALUES(@mail_id, @character_id, @item_id, @fragment_count, @open_socket_count, @skill_code_1, @skill_code_2, @skill_code_3, @skill_code_4, @skill_code_5)

        COMMIT
    END TRY
    BEGIN CATCH
        ROLLBACK
    END CATCH

    SET NOCOUNT OFF
