changeSet.sql:
IF EXISTS (SELECT * FROM dbo.sysobjects WHERE id = object_id(N'dbo.update_arena_record') AND OBJECTPROPERTY(id, N'IsProcedure') = 1)
    DROP PROCEDURE dbo.update_arena_record
GO


CREATE PROCEDURE dbo.update_arena_record
    @character_id dbo.id_t,
    @mode dbo.id_t,
    @score INT,
    @win INT,
    @lose INT,
    @draw INT
AS
    SET NOCOUNT ON

	UPDATE dbo.Arena_Records
	SET score = @score, win = @win, lose = @lose, draw = @draw
	WHERE character_id = @character_id

    SET NOCOUNT OFF
