changeSet.sql:
IF EXISTS (SELECT * FROM dbo.sysobjects WHERE id = object_id(N'dbo.update_building_state') AND OBJECTPROPERTY(id, N'IsProcedure') = 1)
    DROP PROCEDURE dbo.update_building_state
GO


CREATE PROCEDURE dbo.update_building_state
    @building_id dbo.id_t,
    @state TINYINT,
    @start_build_at BIGINT,	
    @expire_at BIGINT,
    @current_hp INT
AS
    SET NOCOUNT ON

    UPDATE dbo.Buildings
   	SET hp = @current_hp, state = @state, build_at = @start_build_at, expire_at = @expire_at
        WHERE id =  @building_id       

    SET NOCOUNT OFF
