changeSet.sql:
IF EXISTS (SELECT * FROM dbo.sysobjects WHERE id = object_id(N'dbo.add_cooldown_effect') AND OBJECTPROPERTY(id, N'IsProcedure') = 1)
    DROP PROCEDURE dbo.add_cooldown_effect
GO


CREATE PROCEDURE dbo.add_cooldown_effect
    @acoount_id dbo.id_t,
    @character_id dbo.id_t,    
    @effect_script_type TINYINT,
    @spell_group TINYINT,
    @value REAL,
    @tick INT,
    @expire_activate_at BIGINT

AS
    SET NOCOUNT ON

    INSERT INTO dbo.Cooldown_Effect_Infos(account_id, character_id, effect_script_type, spell_group, value, tick, expire_activate_at)  
        VALUES(@acoount_id, @character_id, @effect_script_type, @spell_group, @value, @tick, @expire_activate_at)

    SET NOCOUNT OFF
