changeSet.sql:
IF EXISTS (SELECT * FROM dbo.sysobjects WHERE id = object_id(N'dbo.create_vehicle_info') AND OBJECTPROPERTY(id, N'IsProcedure') = 1)
  DROP PROCEDURE dbo.create_vehicle_info
GO


-- error_code_out:
--   0: succeeded
--   547: invalid Account ID
--   2601: duplicated nickname
--   *: DB error
CREATE PROCEDURE dbo.create_vehicle_info
    @character_id dbo.id_t,
    @account_id dbo.id_t,
    @id dbo.id_t,
    @vehicle_code dbo.code_t,
    @birth_day BIGINT,
    @peak_age TINYINT
AS
    SET NOCOUNT ON

    INSERT INTO Vehicle_infos(account_id, character_id, id, vehicle_code, birth_day, peak_age) VALUES (@account_id, @character_id, @id, @vehicle_code, @birth_day, @peak_age)

    SET NOCOUNT OFF
