changeSet.sql:
IF EXISTS (SELECT * FROM dbo.sysobjects WHERE id = object_id(N'dbo.add_select_recipe_production') AND OBJECTPROPERTY(id, N'IsProcedure') = 1)
  DROP PROCEDURE dbo.add_select_recipe_production
GO

CREATE PROCEDURE dbo.add_select_recipe_production
  @owner_id dbo.id_t,
  @recipe_code dbo.code_t,
  @count TINYINT,
  @complete_at BIGINT
AS

  SET NOCOUNT ON

  INSERT INTO Building_Select_Recipe_Production_Infos(building_id, recipe_code, count, complete_at) VALUES (@owner_id, @recipe_code,
      @count, @complete_at)

  SET NOCOUNT OFF
