changeSet.sql:
IF EXISTS (SELECT * FROM dbo.sysobjects WHERE id = object_id(N'dbo.add_mail_equip_item') AND OBJECTPROPERTY(id, N'IsProcedure') = 1)
        DROP PROCEDURE dbo.add_mail_equip_item
GO


      CREATE PROCEDURE dbo.add_mail_equip_item
        @mail_id dbo.id_t,
        @character_id dbo.id_t,
        @item_id dbo.id_t,
        @item_code dbo.code_t,
        @item_count TINYINT,
        @option_script_1 TINYINT,
        @option_value_1 INT,
        @option_script_2 TINYINT,
        @option_value_2 INT,
        @option_script_3 TINYINT,
        @option_value_3 INT,
        @socket_count TINYINT
      AS
      SET NOCOUNT ON

      BEGIN TRY
      BEGIN TRANSACTION

      INSERT INTO dbo.Mail_Inventories(mail_id, character_id, id, item_code, item_count)
      VALUES (@mail_id, @character_id, @item_id, @item_code, @item_count)

      INSERT INTO dbo.Mail_Equip_Item_Infos(mail_id, character_id, item_id, socket_count, option_script_1, option_value_1, option_script_2, option_value_2, option_script_3, option_value_3)
      VALUES(@mail_id, @character_id, @item_id, @socket_count, @option_script_1, @option_value_1, @option_script_2, @option_value_2, @option_script_3, @option_value_3)

      COMMIT
      END TRY

      BEGIN CATCH
      ROLLBACK
      END CATCH

      SET NOCOUNT OFF
