changeSet.sql:
IF EXISTS (SELECT * FROM dbo.sysobjects WHERE id = object_id(N'dbo.create_guild_rank') AND OBJECTPROPERTY(id, N'IsProcedure') = 1)
    DROP PROCEDURE dbo.create_guild_rank
GO


CREATE PROCEDURE dbo.create_guild_rank
    @guild_id dbo.id_t,
    @rank_id TINYINT,
    @name NVARCHAR(10),
    @rights INT,
    @money_withdrawal_per_day INT
AS
    SET NOCOUNT ON

	INSERT INTO dbo.Guild_Ranks(guild_id, rank_id, name, rights, money_withdrawal_per_day)
	    VALUES(@guild_id, @rank_id, @name, @rights, @money_withdrawal_per_day)

	SET NOCOUNT OFF
