IF EXISTS (SELECT * FROM dbo.sysobjects WHERE id = object_id(N'dbo.add_guild_equip_socket_option') AND OBJECTPROPERTY(id, N'IsProcedure') = 1)
        DROP PROCEDURE dbo.add_guild_equip_socket_option
GO


      CREATE PROCEDURE dbo.add_guild_equip_socket_option
        @guild_id dbo.id_t,
        @item_id dbo.id_t,
        @socket_slot_id TINYINT,
        @option_script_1 TINYINT,
        @option_value_1 INT,
        @option_script_2 TINYINT,
        @option_value_2 INT,
        @option_script_3 TINYINT,
        @option_value_3 INT
      AS
      SET NOCOUNT ON


      INSERT INTO dbo.Guild_Equip_Item_Scoket_Infos(item_id, guild_id, socket_slot_id, option_script_1, option_value_1, option_script_2, option_value_2, option_script_3, option_value_3)
      	VALUES(@item_id, @guild_id, @socket_slot_id, @option_script_1, @option_value_1, @option_script_2, @option_value_2, @option_script_3, @option_value_3)

      SET NOCOUNT OFF
