changeSet.sql:
IF EXISTS (SELECT * FROM dbo.sysobjects WHERE id = object_id(N'dbo.create_guild_bank_right') AND OBJECTPROPERTY(id, N'IsProcedure') = 1)
    DROP PROCEDURE dbo.create_guild_bank_right
GO


CREATE PROCEDURE dbo.create_guild_bank_right
    @guild_id dbo.id_t,
    @rank_id TINYINT,
    @vault_id TINYINT,
    @rights INT,
    @item_withdrawal_per_day INT
AS
    SET NOCOUNT ON

	INSERT INTO dbo.Guild_Bank_Rights(guild_id, rank_id, vault_id, rights, item_withdrawal_per_day)
	    VALUES(@guild_id, @rank_id, @vault_id, @rights, @item_withdrawal_per_day)

	SET NOCOUNT OFF
