changeSet.sql:
IF EXISTS (SELECT * FROM dbo.sysobjects WHERE id = object_id(N'dbo.get_password') AND OBJECTPROPERTY(id, N'IsProcedure') = 1)
	DROP PROCEDURE dbo.get_password
GO


CREATE PROCEDURE dbo.get_password
  @account_id dbo.id_t OUT,
  @encrypted_password VARCHAR(128) OUT,
  @password_salt VARCHAR(20) OUT,
  @user_id NVARCHAR(10)
AS

  SET NOCOUNT ON

  SELECT @account_id = id, @encrypted_password = encrypted_password,
      @password_salt = password_salt
    FROM dbo.Accounts
    WHERE user_id = @user_id;

SET NOCOUNT OFF
