changeSet.sql:
IF EXISTS (SELECT * FROM dbo.sysobjects WHERE id = object_id(N'dbo.insert_repeat_quest_mission') AND OBJECTPROPERTY(id, N'IsProcedure') = 1)
    DROP PROCEDURE dbo.insert_repeat_quest_mission
GO


CREATE PROCEDURE dbo.insert_repeat_quest_mission
    @character_id dbo.id_t,
	@quest_code dbo.id_t,
	@mission_code dbo.code_t,
	@goal_code dbo.code_t,
	@value INT
AS
    SET NOCOUNT ON

	INSERT INTO dbo.Accept_Repeat_Quest_Missions(character_id, quest_code, mission_code, goal_code, value)
        VALUES (@character_id, @quest_code, @mission_code, @goal_code, @value)

	SET NOCOUNT OFF
