changeSet.sql:
IF EXISTS (SELECT * FROM dbo.sysobjects WHERE id = object_id(N'dbo.add_guild_relationship') AND OBJECTPROPERTY(id, N'IsProcedure') = 1)
    DROP PROCEDURE dbo.add_guild_relationship
GO


CREATE PROCEDURE dbo.add_guild_relationship
    @owner_id dbo.id_t,
    @target_id dbo.id_t,
    @relationship_type TINYINT,
    @relationship_at BIGINT
AS
    SET NOCOUNT ON

    INSERT INTO dbo.Guild_Relationship_Infos(owner_id, target_id, relationship_type, relationship_at)
        VALUES(@owner_id, @target_id, @relationship_type, @relationship_at)

    SET NOCOUNT OFF
