changeSet.sql:
IF EXISTS (SELECT * FROM dbo.sysobjects WHERE id = object_id(N'dbo.add_cooldown') AND OBJECTPROPERTY(id, N'IsProcedure') = 1)
    DROP PROCEDURE dbo.add_cooldown
GO


CREATE PROCEDURE dbo.add_cooldown
    @acoount_id dbo.id_t,
    @character_id dbo.id_t,    
    @data_code dbo.code_t,
    @expire_at BIGINT

AS
    SET NOCOUNT ON

    INSERT INTO dbo.Cooldown_Infos(account_id, character_id, data_code, expire_at)  
        VALUES(@acoount_id, @character_id, @data_code, @expire_at)

    SET NOCOUNT OFF
