changeSet.sql:
IF EXISTS (SELECT * FROM dbo.sysobjects WHERE id = object_id(N'dbo.add_inventory_accessory_item') AND OBJECTPROPERTY(id, N'IsProcedure') = 1)
    DROP PROCEDURE dbo.add_inventory_accessory_item
GO


CREATE PROCEDURE dbo.add_inventory_accessory_item
    @account_id dbo.id_t,
    @character_id dbo.id_t,
    @item_id dbo.id_t,
    @item_code dbo.code_t,
    @slot_id TINYINT,
    @item_count TINYINT,
    @expire_at BIGINT,
    @option_script_1 TINYINT,
    @option_value_1 INT,
    @option_script_2 TINYINT,
    @option_value_2 INT,
    @option_script_3 TINYINT,
    @option_value_3 INT,
    @inven_type TINYINT
AS
    SET NOCOUNT ON

    BEGIN TRY
        BEGIN TRANSACTION


        INSERT INTO dbo.Inventories
            (id, account_id, character_id, item_code,  slot_id, item_count, inven_type, expire_at) VALUES
            (@item_id, @account_id, @character_id, @item_code, @slot_id, @item_count, @inven_type, @expire_at)


        INSERT INTO dbo.Accessory_Item_Infos(account_id, character_id, inventory_id, option_script_1, option_value_1, option_script_2, option_value_2, option_script_3, option_value_3)
	    VALUES(@account_id, @character_id, @item_id, @option_script_1, @option_value_1, @option_script_2, @option_value_2, @option_script_3, @option_value_3)

        COMMIT
    END TRY
    BEGIN CATCH
        ROLLBACK
    END CATCH

    SET NOCOUNT OFF
