changeSet.sql:
IF EXISTS (SELECT * FROM dbo.sysobjects WHERE id = object_id(N'dbo.add_guild_member') AND OBJECTPROPERTY(id, N'IsProcedure') = 1)
    DROP PROCEDURE dbo.add_guild_member
GO


CREATE PROCEDURE dbo.add_guild_member
    @character_id dbo.id_t,
    @guild_id dbo.id_t,
    @position TINYINT,
    @rank_id TINYINT,
    @contributiveness INT
AS
    SET NOCOUNT ON

	INSERT INTO Guild_Members(character_id, guild_id, position, rank_id, contributiveness) 
            VALUES(@character_id, @guild_id, @position, @rank_id, @contributiveness)
	SET NOCOUNT OFF
