changeSet.sql:
IF EXISTS (SELECT * FROM dbo.sysobjects WHERE id = object_id(N'dbo.update_guild_rank_rights') AND OBJECTPROPERTY(id, N'IsProcedure') = 1)
    DROP PROCEDURE dbo.update_guild_rank_rights
GO

CREATE PROCEDURE dbo.update_guild_rank_rights
    @guild_id dbo.id_t,
    @rank_id TINYINT, 
    @rights INT,
    @gold_withdrawal INT
AS
    SET NOCOUNT ON

    UPDATE dbo.Guild_Ranks SET rights = @rights, money_withdrawal_per_day = @gold_withdrawal
	WHERE guild_id = @guild_id AND rank_id = @rank_id

    SET NOCOUNT OFF
