changeSet.sql:
IF EXISTS (SELECT * FROM dbo.sysobjects WHERE id = object_id(N'dbo.update_guild_bank_rights') AND OBJECTPROPERTY(id, N'IsProcedure') = 1)
    DROP PROCEDURE dbo.update_guild_bank_rights
GO

CREATE PROCEDURE dbo.update_guild_bank_rights
    @guild_id dbo.id_t,
    @rank_id TINYINT, 
    @vault_Id TINYINT, 
    @rights INT,
    @item_withdrawal INT
AS
    SET NOCOUNT ON

    UPDATE dbo.Guild_Bank_Rights SET rights = @rights, item_withdrawal_per_day = @item_withdrawal 
	WHERE guild_id = @guild_id AND rank_id = @rank_id AND vault_Id = @vault_Id

    SET NOCOUNT OFF
